describe FriendsController do

  # This should return the minimal set of attributes required to create a valid
  # Friend. As you add validations to Friend, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all friends as @friends" do
      friend = Friend.create! valid_attributes
      get :index
      assigns(:friends).should eq([friend])
    end
  end

  describe "GET show" do
    it "assigns the requested friend as @friend" do
      friend = Friend.create! valid_attributes
      get :show, :id => friend.id.to_s
      assigns(:friend).should eq(friend)
    end
  end

  describe "GET new" do
    it "assigns a new friend as @friend" do
      get :new
      assigns(:friend).should be_a_new(Friend)
    end
  end

  describe "GET edit" do
    it "assigns the requested friend as @friend" do
      friend = Friend.create! valid_attributes
      get :edit, :id => friend.id.to_s
      assigns(:friend).should eq(friend)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Friend" do
        expect {
          post :create, :friend => valid_attributes
        }.to change(Friend, :count).by(1)
      end

      it "assigns a newly created friend as @friend" do
        post :create, :friend => valid_attributes
        assigns(:friend).should be_a(Friend)
