        Friend.any_instance.stub(:save).and_return(false)
        post :create, :friend => {}
        assigns(:friend).should be_a_new(Friend)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Friend.any_instance.stub(:save).and_return(false)
        post :create, :friend => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested friend" do
        friend = Friend.create! valid_attributes
        # Assuming there are no other friends in the database, this
        # specifies that the Friend created on the previous line
        # receives the :update_attributes message with whatever params are
        # submitted in the request.
        Friend.any_instance.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => friend.id, :friend => {'these' => 'params'}
      end

      it "assigns the requested friend as @friend" do
        friend = Friend.create! valid_attributes
        put :update, :id => friend.id, :friend => valid_attributes
